//var left_side_width = 220; //Sidebar width in pixels
"use strict";

$(function () {
    //Enable sidebar toggle
    $("[data-toggle='offcanvas']").on('click', function (e) {
        e.preventDefault();
        //If window is small enough, enable sidebar push menu
        if ($(window).width() <= 992) {
            $('.row-offcanvas').toggleClass('active').toggleClass("relative");
            $('.left-side').removeClass("collapse-left");
            $(".right-side").removeClass("strech");

        } else {
            //Else, enable content streching
            $('.left-side').toggleClass("collapse-left");
            $(".right-side").toggleClass("strech");
        }
    });
    //Add hover support for touch devices
    $('.btn').on('touchstart', function () {
        $(this).addClass('hover');
    }).on('touchend', function () {
        $(this).removeClass('hover');
    });
    //Activate tooltips
    $("[data-toggle='tooltip']").tooltip();
    /*
     * Add collapse and remove events to boxes
     */
    $("[data-widget='collapse']").on('click', function () {
        //Find the box parent
        var box = $(this).parents(".box").first();
        //Find the body and the footer
        var bf = box.find(".box-body, .box-footer");
        if (!box.hasClass("collapsed-box")) {
            box.addClass("collapsed-box");
            bf.slideUp();
        } else {
            box.removeClass("collapsed-box");
            bf.slideDown();
        }
    });
    /*
     * ADD SLIMSCROLL TO THE TOP NAV DROPDOWNS
     * ---------------------------------------
     */
    $(".navbar .menu").slimscroll({
        height: "200px",
        alwaysVisible: true,
        size: "3px"
    }).css("width", "100%");
    /*
     * INITIALIZE BUTTON TOGGLE
     * ------------------------
     */
    $('.btn-group[data-toggle="btn-toggle"]').on('each', function () {
        var group = $(this);
        $(this).find(".btn").on('click', function (e) {
            group.find(".btn.active").removeClass("active");
            e.preventDefault();
        });
    });
    $("[data-widget='remove']").on('click', function () {
        //Find the box parent
        var box = $(this).parents(".box").first();
        box.slideUp();
    });

    /*
     * Make sure that the sidebar is streched full height
     * ---------------------------------------------
     * We are gonna assign a min-height value every time the
     * wrapper gets resized and upon page load. We will use
     * Ben Alman's method for detecting the resize event.
     *
     **/
    function _fix() {
        //Get window height and the wrapper height
        var height = $(window).height() - $("body > .header").height();
        $(".wrapper").css("min-height", height + "px");
        var content = $(".wrapper").height();
        //If the wrapper height is greater than the window
        if (content > height)
        //then set sidebar height to the wrapper
            $(".left-side, html, body").css("min-height", content + 1 + "px");
        else {
            //Otherwise, set the sidebar to the height of the window
            $(".left-side, html, body").css("min-height", height + 1 + "px");
        }
    }

    //Fire upon load coreplus
    _fix();
    //Fire when wrapper is resized
    $(".wrapper").on('resize', function () {
        _fix();
        fix_sidebar();
    });
    //Fix the fixed layout sidebar scroll bug
    fix_sidebar();
});

function fix_sidebar() {
    //Make sure the body tag has the .fixed class
    if (!$("body").hasClass("fixed")) {
        return;
    }
    //Add slimscroll
    $(".sidebar").slimscroll({
        height: ($(window).height() - $(".header").height()) + "px",
        color: "rgba(0,0,0,0.2)"
    });
}
/*END DEMO*/
